cy.get(_selectors.default.cart.badge).then($badge => {
  const itemCount = parseInt($badge.text());
  if (itemCount > 0) {
    cy.get(_selectors.default.cart.removeButton).each($btn => {
      cy.wrap($btn).click();
    });
    cy.get(_selectors.default.cart.badge).should('not.exist');
    cy.log('Todos los productos han sido eliminados del carrito correctamente ✅');
  } else {
    cy.log('El carrito ya está vacío.');
  }
});

const addedProducts = [];
// 1. Seleccionamos los productos disponibles
cy.get(_selectors.default.products.inventoryItem).then($items => {
  const total = $items.length;
  const randomCount = Math.floor(Math.random() * total) + 1;
  // 2. Elegimos productos aleatorios
  const selectedItems = Cypress._.sampleSize($items.toArray(), randomCount);
  selectedItems.forEach(item => {
    const productName = Cypress.$(item).find(_selectors.default.products.itemName).text().trim();
    addedProducts.push(productName);
    // 3. Agregar cada producto al carrito
    cy.wrap(item).within(() => {
      cy.get(_selectors.default.products.itemButton).click();
    });
  });
})
// 4. Guardamos el array dentro del flujo de Cypress
.then(() => {
  cy.wrap(addedProducts).as('addedProducts');
});
// 5. Ahora vamos al carrito
cy.get(_selectors.default.cart.link).click();
cy.shouldBeVisible(_selectors.default.cart.cartList);
// 6. Recuperamos el alias cuando Cypress ya lo tiene cargado
cy.get('@addedProducts').then(addedProducts => {
  // Validar cantidad
  cy.get(_selectors.default.cart.cartItem).should('have.length', addedProducts.length);
  // Validar que los productos estén en el carrito
  cy.get(_selectors.default.cart.cartItem).each($item => {
    const nameInCart = Cypress.$($item).find(_selectors.default.products.itemName).text().trim();
    expect(addedProducts).to.include(nameInCart);
    cy.log(`Producto en el carrito: ${nameInCart} ✅`);
  });
});
