cy.clickIn(_selectors.default.checkoutOverview.continue);
cy.waitSeconds(1);
if (cy.shouldBeVisible(_selectors.default.checkoutOverview.errorMsg)) {
  cy.fillOutCheckoutForm(_data.default.buyer.firstName, _data.default.buyer.lastName, _data.default.buyer.zip);
  cy.clickIn(_selectors.default.checkoutOverview.continue);
  cy.shouldHaveUrl('include', '/checkout-step-two.html');
} else {
  cy.log('No se mostró ningún mensaje de error al intentar continuar sin completar el formulario.');
}

cy.clickIn(_selectors.default.checkoutOverview.continue);
cy.shouldHaveText(_selectors.default.checkoutOverview.errorMsg, 'Error: First Name is required').then(() => {
  cy.log('El mensaje de error "Error: First Name is required" se mostró correctamente al intentar continuar sin completar el campo Nombre.');
  cy.writeIn(_selectors.default.checkoutOverview.formName, _data.default.buyer.firstName);
  cy.clickIn(_selectors.default.checkoutOverview.continue);
  cy.shouldHaveText(_selectors.default.checkoutOverview.errorMsg, 'Error: Last Name is required').then(() => {
    cy.log('El mensaje de error "Error: Last Name is required" se mostró correctamente al intentar continuar sin completar el campo Apellido.');
    cy.writeIn(_selectors.default.checkoutOverview.formLastName, _data.default.buyer.lastName);
    cy.clickIn(_selectors.default.checkoutOverview.continue);
    cy.shouldHaveText(_selectors.default.checkoutOverview.errorMsg, 'Error: Postal Code is required').then(() => {
      cy.log('El mensaje de error "Error: Postal Code is required" se mostró correctamente al intentar continuar sin completar el campo Código Postal.');
      cy.writeIn(_selectors.default.checkoutOverview.formZip, _data.default.buyer.zip);
      cy.clickIn(_selectors.default.checkoutOverview.continue);
      cy.shouldHaveUrl('include', '/checkout-step-two.html');
    });
  });
});
