let calculatedSum = 0;
cy.get(_selectors.default.checkoutOverview.cartItem).each($item => {
  cy.wrap($item).find(_selectors.default.checkoutOverview.itemPrice).invoke('text').then(priceText => {
    const price = parseFloat(priceText.replace('$', '').trim());
    calculatedSum += price;
  });
}).then(() => {
  cy.get(_selectors.default.checkoutOverview.subtotal).invoke('text').then(subtotalText => {
    const subtotal = parseFloat(subtotalText.replace('Item total: $', '').trim());
    expect(subtotal).to.eq(calculatedSum);
  });
  cy.get(_selectors.default.checkoutOverview.tax).invoke('text').then(taxText => {
    const tax = parseFloat(taxText.replace('Tax: $', '').trim());
    cy.get(_selectors.default.checkoutOverview.total).invoke('text').then(totalText => {
      const total = parseFloat(totalText.replace('Total: $', '').trim());
      expect(total).to.eq(calculatedSum + tax);
    });
  });
});

